StructuredTextModel.Text:
IF fonk_enable THEN
	r_edge_1(Clk:=fonk_enable, Q=>init);
	IF init THEN
		ilk_tartim :=tartim;
	END_IF;
	
	timer_on_1(In:=timer_start, PT:=period, Q=>time_ok, ET=>elapsed_time);
	timer_start := TRUE;
	
	IF time_ok THEN
	
		timer_start := FALSE;
		son_tartim:=tartim;
		fark_tartim := son_tartim - ilk_tartim;
		ilk_tartim := son_tartim;
		IF  fark_tartim>hedef THEN
			artma_yetersiz := FALSE;
		ELSE
			artma_yetersiz :=TRUE;
		END_IF;
	
	END_IF;
ELSE
	artma_yetersiz := FALSE;

END_IF;
