StructuredTextModel.Text:
IF init THEN
	malzeme_bitti := FALSE;
END_IF;
	
(*sikolan gönderme kısmı*)
IF start  THEN

	vana_tarti:=TRUE;
	rotary:=TRUE;
	blower:=TRUE;



	//eksilme kontrolü vib  çalıştrma
	IF in_eksilme THEN
		vib_motor:=TRUE;
	ELSE
		vib_motor:=FALSE;
	END_IF;

	IF bb_tarti<=0.2 AND bb_tarti>=-0.2 THEN
		malzeme_bitti:=TRUE;
		(*burada süre eklendi, belki siklon kilo ile karşılaştırma yapılmalı*)
	END_IF;

	malzeme_timer(In:=malzeme_bitti,PT:=malzeme_time,Q=>m_timer_on);

	IF m_timer_on THEN
		vana_tarti:=FALSE;
		rotary:=FALSE;
		blower:=FALSE;
		vib_motor:=FALSE;
	END_IF;		

ELSE
	vana_tarti:=FALSE;
	rotary:=FALSE;
	blower:=FALSE;
	vib_motor:=FALSE;

END_IF;
