StructuredTextModel.Text:
IF start THEN
	
	kapak := TRUE;

	t1_ins(In:=kapak_açık, PT:=T#500ms);

	IF t1_ins.Q THEN
		bunker_vana := TRUE;
		vibrator_on := TRUE;
	END_IF;
	tmr_2(In:=(tartim<esik_deger), PT:=sirkeleme_zamani, Q=>temp_bos, ET=>et);
	IF temp_bos  THEN
		kapak := FALSE;
		bunker_vana := FALSE;
		vibrator_on := FALSE;
		bosaltma_ok := TRUE;
	END_IF;
	
	
	
ELSE
		kapak := FALSE;
		bunker_vana := FALSE;
		vibrator_on := FALSE;
END_IF;
