StructuredTextModel.Text:
IF  adim = 5  THEN
	adim := 1;
	bosaltma_ok := FALSE;
	Done := FALSE;
END_IF;

IF execute THEN
	servo_in_pozisyon := pozisyon_ok OR (servo_pozisyon<= (pozisyon_set+1.0) AND servo_pozisyon>=(pozisyon_set - 1.0));

	klape_aç := servo_in_pozisyon AND execute;

	t1_ins(In:=klape_aç,PT:=T#2s);

	bunker_klape := t1_ins.Q;

	t2_ins(In := bunker_klape,PT := T#2s);

	riseEdge1(clk :=t2_ins.Q);

	IF riseEdge1.Q THEN
		adim := 1;
	END_IF;

	CASE adim OF
		1 :	 vibrator_on := TRUE;
			 t3_ins(In := vibrator_on, PT := T#5s);
			IF t3_ins.Q THEN
				adim := 2;
				vibrator_on := FALSE;
			END_IF;
		2: t4_ins(In := TRUE, PT := T#5s);
			IF t4_ins.Q THEN
				adim := 3 ;
			END_IF;
		3: IF  agirlik<=10 THEN
				adim := 4;
		    ELSE
				adim := 1;
		    END_IF;
		4 : bosaltma_ok := TRUE;
			adim := 5;
	END_CASE;

	IF bosaltma_ok THEN
		klape_aç := FALSE;
		bunker_klape := FALSE;
		vibrator_on := FALSE;
		Done := TRUE;
	END_IF;
ELSE
		klape_aç := FALSE;
		bunker_klape := FALSE;
		vibrator_on := FALSE;
END_IF;
