StructuredTextModel.Text:
IF init THEN	
	tartim_ok := FALSE;
	hedef := 0.0 ;
	dara_weight := 0.0 ;
	tartim_miktari := 0.0;
END_IF;

if tartim_start = TRUE  THEN
	hedef := dara_weight + set_wegiht ;
	set_uzaklik := hedef - gercek_weight;
	
	if gercek_weight >= hedef THEN
		tartim_ok := TRUE;	
		tartim_miktari := gercek_weight - dara_weight;
	end_if;	
	
	r_trg_0(Clk:=tartim_ok, Q=>tartim_ok_puls);
	
	IF tartim_ok_puls THEN
			dara_weight := gercek_weight ;
	END_IF;		
	
END_IF;
