StructuredTextModel.Text:
(* reçete kayıt bölümü*)
IF receteNo>0 AND   receteNo<=50 and receteKay=TRUE THEN
	FOR indeks:=1 TO 40 DO		
			point:= (receteNo-1)*40+indeks;
			receteM[point]:=m_rec_arr[indeks];
	END_FOR;
	receteKay:=FALSE;
END_IF;

(* receteyi geri yükleme*)

IF recover_prgNo<>programNo THEN
	prg_no_degisti:=TRUE;			
END_IF;
recover_prgNo:=programNo;


IF programNo>0 AND   programNo<=50  and prg_no_degisti=TRUE THEN
	FOR indeks:=1 TO 40 DO		
				point:= (programNo-1)*40+indeks;
				b_recete_setM[indeks]:=receteM[point];
				m_tank_ekr_arr[indeks]:= b_recete_setM[indeks]; (*daha önce alttaydı buraya alındı*)
	END_FOR;
	prg_no_degisti := FALSE;
END_IF;

IF m_prg_sil THEN
	a:=(receteNo-1)*40 + 1;
	b:=(receteNo*40);
	FOR point:= a TO b DO
		receteM[point]:=0.0;
	END_FOR;
	m_prg_sil:=FALSE;
END_IF;
