StructuredTextModel.Text:
(*rise edge of recete hazırla*)
IF init THEN
	tank_seçim:=1;
	recete_son := FALSE;
END_IF;

(*tank değiştirme*)

IF reçete_hazırla=TRUE AND recete_son = FALSE  THEN
	
	IF tank_değiştir=TRUE  THEN
		tank_seçim:=tank_seçim+1;
		tank_değiştir := FALSE ;
	END_IF ;

	WHILE  b_recete_set[tank_seçim]=0.0   and tank_seçim<tank_sayisi DO
	    tank_seçim := tank_seçim + 1;
	END_WHILE;

	IF tank_seçim>tank_sayisi THEN
		recete_son:=TRUE;
		tank_seçim := 1;
		tartim_set := 0;
	END_IF;
	
	IF recete_son = FALSE THEN
		tartim_set :=b_recete_set[tank_seçim]; 
	END_IF;
	
	IF old_tartim_set <> tartim_set AND recete_son = FALSE THEN
		tank_poz_git:=TRUE;
	END_IF;
	IF recete_son THEN tank_poz_git := FALSE; END_IF;
	
	old_tartim_set := tartim_set;
ELSE
	tartim_set:=0;
	old_tartim_set:=0;	
	END_IF;

//tank4 tartım bitince ya da tank4 set = 0 ise recete_son olsun
