StructuredTextModel.Text:
r_edge_1(Clk:=excute);

IF r_edge_1.Q and state =0 THEN
	state :=1;
END_IF;

IF excute THEN
	CASE state OF
	1: ilk_tartim := tartim;
		state := 2;
	2: 	timer_on_1(In:= TRUE,PT:=period);
		artma_yetersiz := FALSE;
		IF timer_on_1.Q THEN
			son_tartim :=tartim;
			state:=3;
		END_IF;
	3: hedef := ilk_tartim + miktar ;
		IF son_tartim> hedef THEN
		artma_yetersiz := TRUE;
		state :=1 ;
		ELSE
		state := 1;			
		END_IF;
	END_CASE;
	
END_IF;
