StructuredTextModel.Text:
// ilklendirme olmadığı açın sayac sıfırlama
IF sayac <0 OR sayac >3 THEN
	sayac := 0 ;
END_IF;
// stop a basılırsa
IF i_stop = FALSE OR tartim_stop THEN
	temp_start:=FALSE;
	temp_stop:=FALSE;
	temp_pause:=FALSE;
	sayac:=0;
	temp_recete_hazirla :=TRUE;
	temp_start_0 :=FALSE;
	temp_zero := TRUE;
END_IF;
// start a basılırsa
IF i_start THEN
	CASE sayac OF
	    0  : 
		sayac := 1;
		temp_zero :=TRUE;
		temp_start_0:=TRUE;  
		1 : 
		sayac :=2 ;
		temp_start_0 := FALSE;
		temp_start := FALSE;
		temp_pause := TRUE;
		2:
		sayac := 1;
		temp_pause := FALSE;
		temp_start := TRUE;
	END_CASE;
END_IF;
// init rise
IF temp_zero THEN
	o_init:=TRUE;
	temp_zero := FALSE;
END_IF;
// recete hazirla init
r_trg_2(temp_recete_hazirla, init_recete_hazirla);
temp_recete_hazirla := FALSE;
// start
tmr_1(In:=temp_start_0, PT:=T#10ms, Q=>q_tmr_1);
o_start := temp_start OR q_tmr_1;


durum := sayac;
